    public function parseDOM($body)
    {
//        $body = htmlspecialchars($body);
        $doc = new DOMDocument('1.0', 'UTF-8');

// set error level
        $internalErrors = libxml_use_internal_errors(true);
        $doc->loadHTML($body);
        libxml_use_internal_errors($internalErrors);
        $doc->saveHTML();

        $xpath = new DOMXPath ($doc);

        foreach ($xpath->query("/html/body/form/table[2]/tbody/tr[*]") as $section) {
//            $v = mb_convert_encoding($section->nodeValue, 'HTML-ENTITIES', 'UTF-8');
//            dd($v);
            dd($section->nodeValue);
        }

    }
